  let date = new Date(timestamp * 1000);
  // let day = date.getDate()
  let days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  let day = `${days[date.getDay()]}`;
  return day;
}
