function formatDayWithTimestamp(timestamp) {
  let date = new Date(timestamp * 1000);
  // let day = date.getDate()
  let days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  let day = `${days[date.getDay()]}`;
  return day;
}
function formatDates(timestamp) {
  let date = new Date(timestamp * 1000);
  let months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  let info = `${date.getDate()} ${months[date.getMonth()]}`;
  return info;
}

